/**
 * Module Dependencies
 */

var event = require('event');
var domify = require('domify');
var Flip = require('flip');

/**
 * Add the landing sheet
 */

var spreadsheet = require('../landing-sheet/landing-sheet.js');
var landingSheet = document.querySelector('.landing-sheet');
landingSheet.appendChild(spreadsheet.el);

/**
 * Add the verdict
 */

var exitMethods = document.querySelector('.exit-methods');

function bshColor(val) {
    var valFloat;
  	if (val.indexOf("(") > -1) {
        valFloat = -parseFloat(val.slice(1, val.length - 2));
    } else {
        valFloat = parseFloat(val.slice(0, val.length - 1));    
    }

    if (valFloat > 15) {
        return 'buy';
    } else if (valFloat < -15) {
        return 'sell';
    } else {
        return 'hold';
    }
}


// EBITDA Multiple Exit
var ebitdaEl = exitMethods.querySelector('.ebitda-multiple');
var ebitda5year = ebitdaEl.querySelector('.year-5-exit');
var ebitdaDiscountRate = ebitdaEl.querySelector('.discount-rate');
var ebitdaFairValue = ebitdaEl.querySelector('.fair-value');
var ebitdaDiscount = ebitdaEl.querySelector('.discount');

// Spreadsheet to Input
ebitda5year.value = spreadsheet.at('J57').val();
ebitdaDiscountRate.value = spreadsheet.at('J58').val();

spreadsheet.on('change J61', function(val) {
  ebitdaDiscount.textContent = val;
  ebitdaEl.classList.remove('buy');
  ebitdaEl.classList.remove('sell');
  ebitdaEl.classList.remove('hold');
  ebitdaEl.classList.add(bshColor(val));
})

spreadsheet.on('change J59', function(val) {
  ebitdaFairValue.textContent = val;
})

// Input to Spreadsheet
event.bind(ebitda5year, 'input', function(e) {
	var val = e.target.value;
	spreadsheet.at('J57').val(val);
})

event.bind(ebitdaDiscountRate, 'input', function(e) {
	var val = e.target.value;
	spreadsheet.at('J58').val(val);
})


